<?phpuse App\App;/** * Created by PhpStorm. * User: megatron * Date: 2017/6/14 * Time: 下午1:49 */final class Megatron{    public static function run()    {        self::setConst();        self::createDir();        self::importFile();        App::run();    }    /**     * 设置常量     */    private static function setConst()    {        $path = str_replace('\\', '/', __FILE__);        define('MEGATRON_PATH', dirname($path));        define('CONFIG_PATH', MEGATRON_PATH . '/Config');        define('DATA_PATH', MEGATRON_PATH . '/Data');        define('LIB_PATH', MEGATRON_PATH . '/Lib');        define('CORE_PATH', LIB_PATH . '/Core');        define('FUNCTION_PATH', LIB_PATH . '/Function');        define('ROOT_PATH', dirname(MEGATRON_PATH));        //应用目录        define('APP_PATH', ROOT_PATH . '/' . APP_NAME);        define('APP_CONFIG_PATH', APP_PATH . '/Config');        define('APP_CONTROLLER_PATH', APP_PATH . '/Controller');        define('APP_TPL_PATH', APP_PATH . '/Tpl');        define('APP_PUBLIC_PATH', APP_TPL_PATH . '/Public');    }    /**     * 创建目录     */    private static function createDir()    {        $dirs = [            APP_PATH,            APP_CONFIG_PATH,            APP_CONTROLLER_PATH,            APP_TPL_PATH,            APP_PUBLIC_PATH        ];        foreach ($dirs as $v) {            is_dir($v) || mkdir($v, 0777, true);        }    }    /**     * 导入文件     */    private static function importFile()    {        $files = [            FUNCTION_PATH.'/function.php',            CORE_PATH.'/App.class.php'        ];        foreach ($files as $v){            require_once $v;        }    }}Megatron::run();